mapper.select:
SELECT
	(
		SELECT
			A.ID
		FROM (
			SELECT ID, LIKE_TYPE, CANCEL_YN, REG_DATE FROM BOARD_LIKE WHERE 1=1
			AND CONTENTS_TYPE = #{contentsType}
			AND CONTENTS_ID = #{contentsId}
			AND LOGIN_ID = #{loginId}
		) A
		WHERE 1=1
		  AND A.LIKE_TYPE = '01'
		  AND A.CANCEL_YN = 'N'
		ORDER BY A.REG_DATE DESC
		LIMIT 1
	) AS LIKE_ID,
	(
		IF(
			(
				SELECT
					B.ID
				FROM (
					SELECT ID, LIKE_TYPE, CANCEL_YN, REG_DATE FROM BOARD_LIKE WHERE 1=1
					AND CONTENTS_TYPE = #{contentsType}
					AND CONTENTS_ID = #{contentsId}
					AND LOGIN_ID = #{loginId}
				) B
				WHERE 1=1
				AND B.LIKE_TYPE = '01'
				AND B.CANCEL_YN IN ('N')
				LIMIT 1
			)
			,'Y','N'
		)
	)  AS LIKE_YN,
    (

			SELECT LIKE_CNT FROM STORY WHERE ID = #{contentsId}

	) AS LIKE_CNT
FROM DUAL

mapper.update:
UPDATE BOARD_LIKE SET
	 CANCEL_YN = IF(CANCEL_YN='N','Y','N')
	,UPT_ID = #{uptId}
	,UPT_DATE = NOW()
WHERE ID=#{likeId}

UPDATE BOARD_TAGS SET
	DELETE_YN = 'Y',
	UPT_ID=#{uptId},
	UPT_DATE=NOW()
WHERE 1=1
AND CONTENTS_TYPE = #{contentsType}
AND CONTENTS_ID = #{contentsId}
AND TAGS NOT IN

	#{item}
