mapper.select:
/* story > selectStory */



WHERE 1=1
AND A.DELETE_YN = 'N'





		ORDER BY LIKE_CNT DESC, REG_DATE DESC
		LIMIT ${limitNum}


		ORDER BY REG_DATE DESC
		LIMIT ${(pageNo-1)*listNo},${listNo}

/*story > view*/

WHERE 1=1
AND A.DELETE_YN = 'N'

	AND A.ID = #{id}

mapper.insert:
INSERT INTO STORY(
	 CATEGORY_ID
	,MEMBER_CATEGORY_ID
	,TITLE
	,CONTENTS
	,THUMBNAIL_IMG_ID
	,SUMMARY
	,DELETE_YN
	,SECRET_YN
    ,ADMIN_ID
	,REG_ID
	,REG_DATE
	,UPT_ID
	,UPT_DATE
)
VALUES (
	 #{categoryId}
	,#{memberCategoryId}
	,#{title}
	,#{contents}
	,#{thumbnailImgId}
	,#{summary}
	,'N'
	,#{secretYn}
	,IFNULL(#{adminId}, (SELECT ID FROM MEMBER WHERE LOGIN_ID = #{regId}))
	,#{regId}
	,NOW()
	,#{uptId}
	,NOW()
)

mapper.update:
UPDATE STORY SET
	VIEW_CNT = (VIEW_CNT+1)
WHERE ID=#{id}

UPDATE STORY SET
	LIKE_CNT = (LIKE_CNT+1)
WHERE ID=#{id}

UPDATE STORY SET
	LIKE_CNT = (LIKE_CNT-1)
WHERE ID=#{id}
